import React, {useState} from 'react'
import Popup from './popupmodal/popup';
const BotList = () => {
  const [modal, setModal] = useState(false);
  const [botList, setBotList] = useState([])

  const toggle = () => {
    setModal(!modal);
}

const saveBot = (botObj) => {
  let tempList = botList
  tempList.push(botObj)
  localStorage.setItem("botList", JSON.stringify(botList))
  setTaskList(botList)
  setModal(false)
}


  return (
    <>
    <div className='header text-center'>
      <h2>IB Bots</h2>
      <button className='btn btn-primary mt-2'  onClick = {() => setModal(true)}>Create Bot</button>
    </div>

    <div className='task-container'>
    <Popup modal = {modal} toggle = {toggle}></Popup>
    </div>
    </>
    
  );
}

export default BotList
