import React, {useState, useEffect} from 'react'
import Popup from './popupmodal/popup';
import axios from 'axios';
import Card from './Card';

const BotList = () => {
  const [modal, setModal] = useState(false);
  const [botList, setBotList] = useState([])

  const deleteBotHandler = (index,ticker) => {
    const encoded = encodeURI(`https://tradingviewsignal.herokuapp.com/v1/bot1/userinput?ticker=${ticker}`);
    let tempList = botList
    botList.splice(index, 1)
    setBotList(tempList)
    axios.delete(encoded)
         .then(res => {
            window.location.reload()
            console.log(res.data) 
          });
  };

  useEffect(() =>{
    axios.get('https://tradingviewsignal.herokuapp.com/v1/bot1/userinput')
    .then(res => {
      setBotList(res.data);
    })
  },[])


  const addBotHandler = (botData) => {
    axios.post('https://tradingviewsignal.herokuapp.com/v1/bot1/userinput', {
      'ticker': botData.ticker, 'StopLoss': botData.StopLoss, 'TakeProfit': botData.TakeProfit, 'AmountToBeInvested': botData.AmountToBeInvested})
      .then(res => console.log(res))
  };
  const updateBotHandler = (Object) => {
    const encoded = encodeURI(`https://tradingviewsignal.herokuapp.com/v1/bot1/userinput?ticker=${Object.ticker}`);
    axios.put(encoded, {
      'ticker': Object.ticker, 'StopLoss': Object.StopLoss, 'TakeProfit': Object.TakeProfit, 'AmountToBeInvested': Object.AmountToBeInvested})
      .then(res => console.log(res))

  
    // axios.patch(`https://tradingviewsignal.herokuapp.com/v1/bot1/userinput/${Object.ticker}`, {
    //   ticker: Object.ticker, StopLoss: Object.StopLoss, TakeProfit: Object.TakeProfit, AmountToBeInvested: Object.AmountToBeInvested
    // })
    // .then(response => {
    //   console.log(response);
    // })

    }


  const toggle = () => {
    setModal(!modal);
  }

  const saveBot = (botObj) => {
    let tempList = botList
    tempList.push(botObj)
    console.log(botObj)
    addBotHandler(botObj)
    setBotList(botList)
    setModal(false)
  }

  const updateListArray = (obj, index) => {
    let tempList = botList
    tempList[index] = obj
    setBotList(tempList)
    updateBotHandler(obj)
    // window.location.reload()
}


  return (
    <>
    <div className='header text-center'>
      <h2>Interactive Broker's Trading Bots</h2>
      <button className='btn btn-primary mt-2'  onClick = {() => setModal(true)}>Create Bot</button>
    </div>

    <div className='task-container'>
      {botList && botList.map((obj , index) => <Card Object = {obj} index = {index} deleteBot= {deleteBotHandler} updateListArray = {updateListArray} />)}
    </div>
    <Popup modal = {modal} toggle = {toggle} save = {saveBot}></Popup>

    </>
    
  );
}

export default BotList
