import React, { useState, useEffect } from 'react'
import { Modal, ModalBody, ModalHeader, ModalFooter, Button } from 'reactstrap';
import axios from 'axios';

const EditBot = ({modal, toggle, update, Object}) => {

    const updateBotHandler = (ticker) => {
        axios.patch(`https://tradingviewsignal.herokuapp.com/v1/bot1/userinput?ticker=${ticker}`, {
            ticker: Object.ticker, StopLoss: Object.StopLoss, TakeProfit: Object.TakeProfit, AmountToBeInvested: Object.AmountToBeInvested
          })
          .then(response => {
            console.log(response);
          })
        }

    const [ticker, setTicker] = useState('');
    const [stoploss, setStoploss] = useState('');
    const [amount, setAmount] = useState('');
    const [takeprofit, setTakeprofit] = useState('');

    useEffect(() => {

    },[])

    const handleChange = (e) => {
        
        const {name, value} = e.target

        if(name === "ticker"){
            setTicker(value)
        }else if(name === "stoploss"){
            setStoploss(value)
        }else if(name === "amount"){
            setAmount(value)
        }else if(name === "takeprofit"){
            setTakeprofit(value)
        }
    }
    const handleSave = (e) => {
        e.preventDefault()
        let taskObj = {}
        taskObj["ticker"] = ticker
        taskObj["StopLoss"] = stoploss
        taskObj["TakeProfit"] = takeprofit
        taskObj["AmountToBeInvested"] = amount
        update(taskObj)

    }

  return (
    <Modal isOpen={modal} toggle={toggle}>
    <ModalHeader toggle={toggle}>Update Task</ModalHeader>
    <ModalBody>
    
            <div className = "form-group">
                <label>Task Name</label>
                <input type="text" className = "form-control" value = {taskName} onChange = {handleChange} name = "taskName"/>
            </div>
            <div className = "form-group">
                <label>Description</label>
                <textarea rows = "5" className = "form-control" value = {description} onChange = {handleChange} name = "description"></textarea>
            </div>
        
    </ModalBody>
    <ModalFooter>
    <Button color="primary" onClick={handleUpdate}>Update</Button>{' '}
    <Button color="secondary" onClick={toggle}>Cancel</Button>
    </ModalFooter>
</Modal>
  )
}

export default EditBot
