import React, { useState, useEffect } from 'react'
import { Modal, ModalBody, ModalHeader, ModalFooter, Button } from 'reactstrap';
import axios from 'axios';

const EditBot = ({modal, toggle, update, Object}) => {

    const updateBotHandler = (ticker) => {
        axios.patch(`https://tradingviewsignal.herokuapp.com/v1/bot1/userinput?ticker=${ticker}`, {
            ticker: Object.ticker, StopLoss: Object.StopLoss, TakeProfit: Object.TakeProfit, AmountToBeInvested: Object.AmountToBeInvested
          })
          .then(response => {
            console.log(response);
          })
        }

    const [ticker, setTicker] = useState('');
    const [stoploss, setStoploss] = useState('');
    const [amount, setAmount] = useState('');
    const [takeprofit, setTakeprofit] = useState('');

    useEffect(() => {
        updateBotHandler(Object.ticker)
        setTicker(Object.ticker)
        setStoploss(Object.StopLoss)
        set
    },[])

    const handleChange = (e) => {
        
        const {name, value} = e.target

        if(name === "ticker"){
            setTicker(value)
        }else if(name === "stoploss"){
            setStoploss(value)
        }else if(name === "amount"){
            setAmount(value)
        }else if(name === "takeprofit"){
            setTakeprofit(value)
        }
    }
    const handleSave = (e) => {
        e.preventDefault()
        let taskObj = {}
        taskObj["ticker"] = ticker
        taskObj["StopLoss"] = stoploss
        taskObj["TakeProfit"] = takeprofit
        taskObj["AmountToBeInvested"] = amount
        // update(taskObj)

    }

  return (
    <div>
        <Modal isOpen={modal} toggle={toggle}>
            <ModalHeader toggle={toggle}>Update Bot</ModalHeader>
            <ModalBody>
            
                <form>
                    <div className = "form-group" style={{marginBottom: "10px"}}>
                        <label>Ticker:</label>
                        <input type="text" className = "form-control" placeholder='AAPL' value = {ticker} onChange = {handleChange} name = "ticker"/>
                    </div>
                    <div className = "form-group" style={{marginBottom: "10px"}}>
                        <label>Stop Loss:</label>
                        <input type="number" step="0.001" className = "form-control" placeholder='2.0' value = {stoploss} onChange = {handleChange} name = "stoploss"/>
                    </div>
                    <div className = "form-group" style={{marginBottom: "10px"}}>
                        <label>Take Profit:</label>
                        <input type="number" step="0.001" className = "form-control" placeholder='2.0' value = {takeprofit} onChange = {handleChange} name = "takeprofit"/>
                    </div>
                    <div className = "form-group" style={{marginBottom: "10px"}}>
                        <label>Investment:</label>
                        <input type="number" step="0.001" className = "form-control" placeholder='1000' value = {amount} onChange = {handleChange} name = "amount"/>
                    </div>
                </form>
                
            </ModalBody>
            <ModalFooter>

            <Button color="primary" onClick={handleSave}>Update</Button>{' '}
            <Button color="secondary" onClick={toggle}>Cancel</Button>

            </ModalFooter>
        </Modal>
    </div>
  )
}

export default EditBot
